import React from "react";
import Axios from "axios";

class EmployeeList extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      employees: [],
      errorMsg: "",
    };
  }

  componentDidMount() {
    this.getAllEmployees();
  }

  getAllEmployees = () => {
    let dataURL =
      "https://gist.githubusercontent.com/Meghesha/8ce517b65a202e0b2a4142c7364caee8/raw/97c6744f796c564d7de788567e45ebd431f1ac7e/Employees-19-01-2023";
    Axios.get(dataURL)
      .then((response) => {
        this.setState({
          ...this.state,
          employees: response.data,
        });
      })
      .catch((error) => {
        this.setState({
          ...this.state,
          errorMsg: error,
        });
      });
  };

  render() {
    return (
      <React.Fragment>
        <section className="p-3">
          <div className="container">
            <div className="card">
              <div className="card-header">
                <p className="h2 text-primary">Employees Info</p>
                <p className="lead">
                  Lorem ipsum dolor sit amet consectetur adipisicing elit. Esse
                  at a sapiente beatae soluta necessitatibus consequuntur saepe
                  molestiae aut, facilis accusamus ullam perspiciatis velit quod
                  repudiandae iste debitis dolores voluptas accusantium!
                  Nostrum, ut nulla. Sequi ea quis praesentium cumque labore!
                </p>
              </div>
            </div>
            <table className="table table-hover table-striped table-light bg-light text-center">
                <thead className="bg-primary text-white">
                  <tr>
                    <th>SNO</th>
                    <th>Image</th>
                    <th>Full Name</th>
                    <th>Email</th>
                    <th>Gender</th>
                    <th>Country</th>
                    <th>Ip Address</th>
                  </tr>
                </thead>
                <tbody>
                  {this.state.employees.length > 0 ? (
                    <React.Fragment>
                      {
                        this.state.employees.map(employee=>{
                            return(
                                <tr key={employee.id}>
                                    <td>{employee.id}</td>
                                    <td><img src={employee.image} alt="" /></td>
                                    <td><a href={`/employees/${employee.id}`} className='font-weight-bold text-teal'>{employee.first_name} {employee.last_name}</a> </td>
                                    <td>{employee.email}</td>
                                    <td>{employee.gender}</td>
                                    <td>{employee.country}</td>
                                    <td>{employee.ip_address}</td>
                                </tr>
                            )
                        })
                      }
                    </React.Fragment>
                  ) : null}
                </tbody>
              </table>
          </div>
        </section>
      </React.Fragment>
    );
  }
}
export default EmployeeList;
