console.log(id);
    }

    getEmployee = () => {
        let dataURL =
      "https://gist.githubusercontent.com/Meghesha/8ce517b65a202e0b2a4142c7364caee8/raw/97c6744f796c564d7de788567e45ebd431f1ac7e/Employees-19-01-2023";
      Axios.get(dataURL).then((response)=>{
        let employees = response.data;
        let employee = employees.find(employee => {
            return employee.id === this.state.employeeId;
        })
        this.setState({
             ...this.state,
            selectedEmployee : employee
        })
      }).catch((error)=>{
        this.setState({
            ...this.state,
            errorMsg : error.message
        })
      });
    }

    render() {
        return (
            <React.Fragment>
                <section className="p-5">
                    <div className="container">
                        <div className="row">
                            <div className="col">
                                <div className="card">
                                    <div className="card-header bg-teal text-white">
                                        {/* <p className="h4">{selectedEmployee.first_name} {selectedEmployee.last_name}</p> */}
                                        <pre>{this.state.employeeId}</pre>
                                        {/* <pre>{JSON.stringify(this.state.selectedEmployee)}</pre> */}
                                    </div>
                                    <div className="card-body">
                                        <ul className="list-group">
                                            <li className="list-group-item">
                                                {/* Name : {} */}
                                            </li>
                                        </ul>

                                    </div>
                                </div>
                            </div>

                        </div>
                    </div>
                </section>
                
            </React.Fragment>
        );
    }
};
export default EmployeeDetails;
