import React from 'react';
import Axios from 'axios';
// import {withRouter} from 'react-router-dom';
// import { Component } from 'react';

class EmployeeDetails extends React.Component{
    constructor(props) {
        super(props);
        this.state={
            employeeId :null,
            selectedEmployee : {},
            errorMsg : ''
        }
    }
    
    componentDidMount(){
        const employeeId = new URLSearchParams(window.location.search);
        // console.log(employeeId);
        this.setState({
            employeeId : employeeId 
        })
        this.getEmployee();
    }

    getEmployee = () => {
        let dataURL =
      "https://gist.githubusercontent.com/Meghesha/8ce517b65a202e0b2a4142c7364caee8/raw/97c6744f796c564d7de788567e45ebd431f1ac7e/Employees-19-01-2023";
      Axios.get(dataURL).then((response)=>{
        let employees = response.data;
        let employee = employees.find(employee => {
            return employee.id === this.state.employeeId;
        })
        this.setState({
             ...this.state,
            selectedEmployee : employee
        })
      }).catch((error)=>{
        this.setState({
            ...this.state,
            errorMsg : error.message
        })
      });
    }

    render() {
        const {employeeId} = this.state;
        return (
            <React.Fragment>
            <pre>{JSON.stringify(this.state.selectedEmployee)}</pre>

                {
                    employeeId && (
                        <ul>
                            {Array.from(employeeId.keys()).map((key, index)=>{
                                return(
                                <li key={index}>{key}:{employeeId.get(key)}</li>
                                )
                            })}
                        </ul>
                    )
                }  
                {this.state.selectedEmployee.length > 0 ? 
                <React.Fragment>
                    {this.state.selectedEmployee.map(employee => {
                        return(
                            <tr key={employee.id}>
                                    <td>{employee.id}</td>
                                    {/* <td><img src={employee.image} alt="" /className='font-weight-bold text-teal'>{></td>
                                    <td><a href={`/employees/${employee.id}`} employee.first_name} {employee.last_name}</a> </td> */}
                                    <td>{employee.email}</td>
                                    <td>{employee.gender}</td>
                                    <td>{employee.country}</td>
                                    <td>{employee.ip_address}</td>
                                </tr>
                        )
                    })}
                </React.Fragment>: null}
            </React.Fragment>
        );
    }
};
export default EmployeeDetails;
